#include "iostream"
#include "vector"
#include <set>
#include <map>
#include <unordered_set>
#include <algorithm>
using namespace std;

class Solution {
public:
	bool isMatch(string s, string p) {
		int slen = s.length();
		int plen = p.length();
		vector<vector<bool>> f(slen + 1, vector<bool>(plen + 1, false));
		f[0][0] = true;
		for (int i = 1; i <= slen; i++)
			f[i][0] = false;
		for (int j = 1; j <= plen; j++)
			f[0][j] = f[0][j - 1] && p[j - 1] == '*';
		for (int i = 1; i <= slen; i++)
			for (int j = 1; j <= plen; j++)
			{
				if (p[j - 1] == '*')
					f[i][j] = f[i - 1][j] || f[i][j - 1];
				else
				{
					if (p[j - 1] == '?') f[i][j] = f[i - 1][j - 1];
					else f[i][j] = f[i - 1][j - 1] && p[j - 1] == s[i - 1];
				}
			}
		return f[slen][plen];
	}
};

int main()
{
	Solution s;
	string s1 = "";
	string s2 = "**";
	cout << s.isMatch(s1, s2) << endl;
    cin.get();
}
