#include "iostream"
#include "vector"
#include <set>
#include <map>
#include <unordered_set>
#include <algorithm>
using namespace std;

class KMP {
public:
	int strStr(string haystack, string needle) {
		if (needle.empty()) return 0;
		int lh = haystack.length();
		int ln = needle.length();
		int next[ln];
		compute_prefix_function(needle, next);
		int k = -1;
		for (int i = 0;i < lh;i++)
		{
			while (k >= 0 && needle[k + 1] != '?' && needle[k + 1] != haystack[i])
				k = next[k];
			if (needle[k + 1] == '?' || haystack[i] == needle[k + 1])
				k++;
			if (k == ln - 1)
				return i - ln + 1;
		}
		return -1;
	}
private:
	void compute_prefix_function(string needle, int * next)
	{
		int ln = needle.length();
		next[0] = -1;
		int k = -1;
		for (int i = 1;i < ln;i++)
		{
			while (k >= 0 && needle[i] != '?' && needle[k + 1] != '?' && needle[i] != needle[k + 1])
				k = next[k];
			if (needle[k + 1] == '?' || needle[i] == '?' || needle[k + 1] == needle[i])
				k++;
			next[i] = k;
		}
	}
};

class Solution {
public:
	bool isMatch(string s, string p) {
		int slen = s.length();
		int plen = p.length();
		int sleft, sright, pleft, pright;
		sleft = pleft = 0;
		sright = slen - 1;
		pright = plen - 1;
		while (pleft <= pright && p[pleft] != '*')
		{
			if (sleft > sright) return false;
			if (p[pleft] == '?' || p[pleft] == s[sleft])
			{
				pleft++;
				sleft++;
			}
			else return false;
		}
		if (pleft > pright && sleft <= sright) return false;
		while (pleft <= pright && p[pright] != '*')
		{
			if (sleft > sright) return false;
			if (p[pright] == '?' || p[pright] == s[sright])
			{
				pright--;
				sright--;
			}
			else return false;
		}
		if (pleft > pright && sleft <= sright) return false;
		KMP k;
		while (pleft <= pright)
		{
			while (pleft<=pright && p[pleft] == '*') pleft++;
			if (pleft > pright) break;
			int start_pos = pleft;
			while (p[pleft] != '*')  pleft++;
			string needle = p.substr(start_pos, pleft - start_pos);
			string haystack = s.substr(sleft, sright + 1 - sleft);
			int pos = k.strStr(haystack, needle);
			if (pos == -1) return false;
			sleft = sleft + pos + pleft - start_pos;
		}
		return true;
	}
};

int main()
{
	Solution s;
	string s1 = "";
	string s2 = "**";
	cout << s.isMatch(s1, s2) << endl;
    cin.get();
}
